void second_form::set_diffculty(QString rawInput){
    set_valid(false);
    if (rawInput == "Hard" || rawInput == "hard"){
        set_numOfTrys(30);
        ui->tryCountLabel->setText("30");
        ui->outOfLabel->setText("out of 30");
        valid = true;
    }
    if (rawInput == "Medium" || rawInput == "medium"){
        set_numOfTrys(60);
        ui->tryCountLabel->setText("60");
        ui->outOfLabel->setText("out of 60");
        valid = true;
    }
    if (rawInput == "Easy" || rawInput == "easy"){
        set_numOfTrys(100);
        ui->tryCountLabel->setText("100");
        ui->outOfLabel->setText("out of 100");
        valid = true;
    }
    if (valid != false){
        set_q_num(1);
    }
}

//_____________________________________________
//User presses >ENTER<
void second_form::on_command_line_returnPressed()
{
    QString userInput;

    switch (get_q_num()) {
    case 0:
        //all 5 ships are randomly generated and defined
        set_ships();
        break;
    //sets difficulty for game
    case 1:
        //retrieves text from command_line
        userInput = ui->command_line->text();
        //validates and defines the game difficulty(number of trys)
        set_diffculty(userInput);
        break;
        //case 2 continues to loop until the difficulty is set
    case 2:
        //retrieves text from command_line
        userInput = ui->command_line->text();
        //validates user input
        set_player_input(userInput);
        //if input is valid - places hit or miss marker at input coordinate
        is_ship_hit();
        break;
    case 3:
        //gets text from command_line
        userInput = ui->command_line->text();
        //defines the variable used for input
        set_player_input(userInput);
        //refreshes grid with hit or miss markers
        is_ship_hit();
        break;
    }

    //error & validation handling
    if (valid == true)
    {
        //if difficulty is valid and defined
        if (get_q_num() == 1){
            set_q_num(2);
            ui->trysLabel->setText("TRY COUNTER");
        }
    }
    if (valid == false)
    {
        if (get_q_num() == 0){
            ui->label_top->setText("Enter Difficulty: 'Hard' 'Medium' or 'Easy'");
            ui->label_bottom->setText("Invalid Entry, Please Try Again.");
        }
        if (get_q_num() == 1){
            ui->label_top->setText("Enter Coordinate");
            ui->label_bottom->setText("Invalid Entry, Valid Ex. 'A1' or 'a1'");
        }
    }
    //clears text from command line
    ui->command_line->setText("");

}
